import React from 'react'
import {Link} from 'react-router-dom'

import useBasketCart from 'hooks/basketCart/use-basket-cart'
import useBasket from 'hooks/Basket/use-basket'


const BasketCart = () => {
  const { totalBasketCount, totalPrice } = useBasketCart()
  const { cleanBasket } = useBasket()

  return (
    <div className='cart'>
      <div className='dropdown'>
        <Link
          to={'/basket'}
          className='dLabel btn btn-inverse btn-block btn-lg'
        >
          <i className='fa fa-shopping-cart'/>
          <span className='cart-text'>{totalBasketCount} item(s) - ${totalPrice}</span>
        </Link>

        <button
          className='btn btn-danger btn-block btn-cleanBasket'
          onClick={cleanBasket}
        >
          Clean Cart
        </button>
      </div>
    </div>
  )
};

export default BasketCart
