import React from 'react'
import {Link} from 'react-router-dom'
import classNames from 'classnames'
import * as R from 'ramda'

import useGetCategories from 'hooks/categories/use-get-categories'
import Loader from 'components/Shared/Loader'


const Categories = () => {

  const { categories, activeCategoryId, } = useGetCategories()

  // returns not completely curried function
  const getActiveState = R.propEq('id', activeCategoryId)

  const renderCategory = (category) => {
    const linkClass = classNames({
      'list-group-item': true,
      'active': getActiveState(category)
    });

    const { id, name } = category;

    return (
      <Link
        to={`/categories/${id}`}
        className={linkClass}
        key={id}
      >
        {name}
      </Link>
    )
  }

  const renderAllCategory = () => {
    const allLinkCLass = classNames({
      'list-group-item': true,
      // return true if activeCategoryId is null or undefined
      'active': R.isNil(activeCategoryId),
    })

    return (
      <Link
        to={'/'}
        className={allLinkCLass}
      >
        All
      </Link>
    )
  }

  return (
    <div className='well'>
      <h4>Brand</h4>
      <div className='list-group'>
        {categories.length === 0 && <Loader/>}
        {categories.length > 0 && renderAllCategory()}
        {categories.map(renderCategory)}
      </div>
    </div>
  )
}

export default Categories
