import React from 'react'
import {Link} from 'react-router-dom'
import * as R from 'ramda'

import useBasket from 'hooks/basket/use-basket'


const Basket = () => {
  const { phonesInBasket, totalPrice, cleanBasket, removePhoneFromBasketCb, checkoutBasket } = useBasket()

  // rendering
  const isBasketEmpty = R.isEmpty(phonesInBasket);

  const renderEmptyBasket = () => (
    <div>Your shopping cart is empty</div>
  )

  const renderTotalPrice = () => (
    <div className='row'>
      <div className='pull-right total-user-checkout'>
        <b>Total:</b>
        ${totalPrice}
      </div>
    </div>
  )

  const renderBasketTable = () => (
    <>
      <div className='table-responsive'>
        <table className='table-bordered table-striped table-condensed cf'>
          <tbody>
            {phonesInBasket.map((phone) => (
              <tr
                key={phone.id}
                className='item-checkout'
              >
                <td className='first-column-checkout'>
                  <img
                    className='img-thumbnail'
                    src={phone.image}
                    alt={phone.name}
                  />
                </td>

                <td>{phone.name}</td>
                <td>${phone.price}</td>
                <td>{phone.count}</td>
                <td>
                  <span
                    onClick={() => removePhoneFromBasketCb(phone.id)}
                    className='delete-cart'
                  />
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      {renderTotalPrice()}
    </>
  )

  const renderBasketContent = () => (
    <div>
      {isBasketEmpty
        ? (renderEmptyBasket())
        : (renderBasketTable())
      }
    </div>
  )

  const renderBasketSidebar = () => (
    <div>
      <Link
        to={'/'}
        className='btn btn-info'
      >
        <span className='glyphicon glyphicon-info-sign'/>
        <span>Continue shopping!</span>
      </Link>

      {R.not(isBasketEmpty) && (
        <div>
          <button
            onClick={cleanBasket}
            className='btn btn-danger'
          >
            <span className='glyphicon glyphicon-trash'/>
            Clear cart
          </button>

          <button
            className='btn btn-success'
            onClick={() => checkoutBasket(phonesInBasket)}
          >
            <span className='glyphicon glyphicon-envelope'/>
            Checkout
          </button>
        </div>
      )}
    </div>
  )

  return (
    <div className='view-container'>
      <div className='container'>
        <div className='row'>
          <div className='col-md-9'>
            {renderBasketContent()}
          </div>
          <div className='col-md-3'>
            {renderBasketSidebar()}
          </div>
        </div>
      </div>
    </div>
  )
}

export default Basket

