import React from 'react'
import {Link} from 'react-router-dom'
import * as R from 'ramda'


import Loader from 'components/Shared/Loader'
import Error from 'components/Shared/Error'
import BasketCart from 'components/Shared/BasketCart'

import useAddPhoneToBasket from 'hooks/phones/use-add-phone-to-basket'
import useGetPhone from 'hooks/phone/use-get-phone'


const SinglePhone = (props) => {
  const { match: { params: { id } } } = props;

  const { phone, error, isLoading } = useGetPhone(id)
  const addToBasketCb = useAddPhoneToBasket()

  const renderPhoneInfo = () => {
    // compose works from right to left
    const fields = R.compose(
      R.toPairs,
      R.pick([
        'cpu',
        'camera',
        'size',
        'weight',
        'display',
        'battery',
        'memory',
      ])
    )(phone);

    return fields.map(([key, value]) => (
      <div className='column' key={key}>
        <div className='ab-details-title'>
          <p>{key}</p>
        </div>
        <div className='ab-details-info'>
          {value}
        </div>
      </div>
    ))
  }

  const renderContent = () => {
    return (
      <div className='thumbnail'>
        <div className='row'>
          <div className='col-md-6'>
            <img
              className='img-thumbnail'
              src={phone.image}
              alt={phone.name}
            />
          </div>

          <div className='col-md-6'>
            {renderPhoneInfo()}
          </div>
        </div>

        <div className='caption-full'>
          <h4 className='pull-right'>${phone.price}</h4>
          <h4>{phone.name}</h4>
          <p>{phone.description}</p>
        </div>
      </div>
    )
  }

  const renderSidebar = () => (
    <div>
      <p className='lead'>Quick shop</p>
      <BasketCart/>
      <div className='form-group'>
        <h1>{phone.name}</h1>
        <h2>${phone.price}</h2>
      </div>
      <Link
        to={'/'}
        className='btn btn-info btn-block'
      >
        Back to store
      </Link>

      <button
        type='button'
        className='btn btn-success btn-block'
        onClick={() => addToBasketCb(phone.id)}
      >
        Add to cart
      </button>
    </div>
  )

  if (isLoading) return <Loader/>;
  if (error) return <Error/>;

  return (
    <div className='view-container'>
      <div className='container'>
        <div className='row'>
          <div className='col-md-9'>
            {phone && renderContent()}
          </div>

          <div className='col-md-3'>
            {phone && renderSidebar()}
          </div>
        </div>
      </div>
    </div>
  )
};

export default SinglePhone
