import * as R from 'ramda'
import { handleActions } from 'redux-actions'

import {AddPhoneToBasket, RemovePhoneFromBasket, CleanBasket} from './actions'

const initialState = [];

export default handleActions({
  [AddPhoneToBasket]: (state, { payload }) => (
    R.append(payload, state)
  ),

  [RemovePhoneFromBasket]: (state, { payload }) => (
    // R.Of makes an array with given payload
    R.without(R.of(payload), state)
  ),

  [CleanBasket]: () => initialState,
}, initialState);
