import { takeEvery, put, fork, call } from 'redux-saga/effects';

import {FetchCategoriesStart, FetchCategoriesSuccess, FetchCategoriesFailure} from './actions'
import {fetchCategories as fetchCategoriesApi} from 'api'


function* fetchCategoriesFlow() {
  try {
    const categories = yield call(fetchCategoriesApi);
    yield put(FetchCategoriesSuccess(categories));
  } catch (err) {
    yield put(FetchCategoriesFailure(err));
  }
}

function* fetchCategories() {
  yield takeEvery(FetchCategoriesStart, fetchCategoriesFlow)
}

export default function* () {
  yield fork(fetchCategories)
}
