import * as R from 'ramda'
import { handleActions } from 'redux-actions'

import {SearchPhoneSuccess} from './actions'

const initialState = {
  searchTerm: '',
};

export default handleActions({
  [SearchPhoneSuccess]: (state, { payload }) => (
    R.mergeRight(state, {
      searchTerm: payload,
    })
  ),
}, initialState);
