import { takeEvery, put, fork, call } from 'redux-saga/effects';

import {SearchPhoneStart, SearchPhoneSuccess, SearchPhoneFailure} from './actions'
import {searchPhone as searchPhoneApi} from 'api'


function* searchPhoneFlow({ payload: text }) {
  try {
    yield call(searchPhoneApi, text);
    yield put(SearchPhoneSuccess(text));
  } catch (err) {
    yield put(SearchPhoneFailure(err));
  }
}

function* searchPhone() {
  yield takeEvery(SearchPhoneStart, searchPhoneFlow)
}

export default function* () {
  yield fork(searchPhone)
}
