import * as R from 'ramda'
import { handleActions } from 'redux-actions'

import {FetchPhoneByIdSuccess} from './actions'

const initialState = {
  id: null,
};

export default handleActions({
  [FetchPhoneByIdSuccess]: (state, { payload }) => (
    R.mergeRight(state, {
      id: R.prop('id', payload),
    })
  ),
}, initialState);
