import { takeEvery, put, fork, call } from 'redux-saga/effects';

import {FetchPhoneByIdStart, FetchPhoneByIdSuccess, FetchPhoneByIdFailure} from './actions'
import {getPhoneById as getPhoneByIdApi} from 'api'


function* fetchPhoneByIdFlow({ payload }) {
  const id = payload;

  try {
    const phone = yield call(getPhoneByIdApi, id);
    yield put(FetchPhoneByIdSuccess(phone));
  } catch (err) {
    yield put(FetchPhoneByIdFailure(err));
  }
}

function* fetchPhoneById() {
  yield takeEvery(FetchPhoneByIdStart, fetchPhoneByIdFlow)
}

export default function* () {
  yield fork(fetchPhoneById)
}
